import streamlit as st


st.title("Streamlit Layouts")
st.subheader("Organize your app with different layout options.")

col1, col2 = st.columns(2)


with col1:
    st.header("Masala Chai")
    st.image("data:image/jpeg;base64,",width=200)
    vote1=st.button("Vote for Masala Chai")

with col2:
   st.header("Ginger Tea")
   st.image("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTgYNZIhCP9bs0PWRCP4cHfKXsbNT_WcrTY6w&s",width=200)
   vote2=st.button("Vote for Ginger Tea")

if vote1:
    st.success("You voted for Masala Chai")

elif vote2:
    st.success("You voted for Ginger Tea")

name = st.sidebar.text_input("Enter your name")
tea = st.sidebar.selectbox("Choose Your Chai ",
                           [
                                 "Masala Chai",
                                 "Ginger Tea",
                                 "Lemon Tea",
                                 "Green Tea",
                                 "Black Tea"
                           ])

st.write(f"Welcome {name} and your {tea} chai is getting ready !")

with st.expander("Show Chai making instructions"):
    st.write("""
            1. Boil water
            2. Add tea leaves and spices
            3. Add milk and sugar
            4. Let it simmer for a few minutes
            5. Strain and serve hot
            """)
    
st.markdown("### Welcome to Chai App")
st.markdown('> Blockquote')